"use strict";const langComboBox=document.querySelector(".dropdown.nav-bar__languages");const itemOrderCount=document.querySelector("#itemOrderCount");const ready=callback=>{if(document.readyState!=="loading"){callback()}else if(document.addEventListener){document.addEventListener("DOMContentLoaded",callback)}else{document.attachEvent("onreadystatechange",function(){if(document.readyState==="complete"){callback()}})}};const isObjectInViewport=obj=>{const rect=obj.getBoundingClientRect();return rect.top>=0&&rect.left>=0&&rect.top<=(window.innerHeight||document.documentElement.clientHeight)&&rect.left<=(window.innerWidth||document.documentElement.clientWidth)};const lazyLoader=selector=>{for(const image of document.querySelectorAll(selector)){if(isObjectInViewport(image)){if(image.getAttribute("data-src")){image.setAttribute("src",image.getAttribute("data-src"));image.removeAttribute("data-src")}if(image.getAttribute("data-srcset")!==null){image.setAttribute("srcset",image.getAttribute("data-srcset"));image.removeAttribute("data-srcset")}image.setAttribute("data-loaded",true)}}};const lazyLoad=()=>{const lazyImages=document.querySelectorAll("[data-src]");if(lazyImages.length>0){const lazyLoad=()=>{lazyLoader("[data-src]",window,document.documentElement)};window.addEventListener("scroll",lazyLoad);window.addEventListener("load",lazyLoad);window.addEventListener("resize",lazyLoad)}};ready(()=>{comboBoxInit(langComboBox);const changeLanguage=event=>{const{link:link}=event.target.dataset;window.location=`${link}`};langComboBox.querySelectorAll(".dropdown__item").forEach(item=>{item.addEventListener("click",changeLanguage)})});