"use strict";const navBar=document.querySelector(".nav-bar"),statusChangers=document.querySelectorAll(".status-changer"),actionSelectorChangeClass='[data-action="change-class"]';const scrollWindow=()=>{const{scrollClass:scrollClass,scrollStart:scrollStart}=navBar.dataset;document.scrollingElement.scrollTop>scrollStart?navBar.classList.add(scrollClass):navBar.classList.remove(scrollClass)};const statusChangerClick=changer=>{const{target:target,className:className}=changer.dataset;changer.classList.toggle(className);document.querySelector(target).classList.toggle(className)};window.addEventListener("scroll",scrollWindow);for(const changer of[...statusChangers]){changer.addEventListener("click",()=>statusChangerClick(changer))}const comboBoxInit=comboBox=>{const comboBoxScreen=comboBox.querySelector('[data-object="screen"]');const comboBoxLabel=comboBox.querySelector('[data-object="label"]');const comboBoxItemsUl=comboBox.querySelector('[data-object="items"]');const comboBoxItems=comboBoxItemsUl.querySelectorAll('[data-object="item"]');comboBoxScreen.addEventListener("click",()=>{comboBoxItemsUl.classList.toggle("active")});const itemClick=event=>{const{target:target}=event;const{id:id}=target.dataset;comboBoxLabel.textContent=target.textContent;comboBoxScreen.setAttribute("data-id",id);comboBoxItemsUl.classList.remove("active")};for(const item of[...comboBoxItems]){item.addEventListener("click",itemClick)}};document.querySelectorAll(actionSelectorChangeClass).forEach(obj=>{obj.addEventListener("click",event=>{const{dataset:{target:target,className:className}}=event.currentTarget;document.querySelector(target).classList.toggle(className)})});